import React, {useState} from 'react'
import ReCAPTCHA from 'react-google-recaptcha'

import '../css/RegistrationForm.css'
import { RECAPTCHA_CLIENT_KEY } from '../constants/configs'

const RegistrationForm = () => {

    const [captchaVisible, setCaptchaVisible] = useState(true)
    const [formValues, setFormValues] = useState(
        {name: '', email: '', password: ''}
    )

    const handleInputChange = e => {
        const {name, value} = e.target
        setFormValues({...formValues, [name]: value})
    }

    const postRegistrationData = async (e) => {
        e.preventDefault()
        const { name, email, password } = formValues
        const response = postRegistrationData(
            {
                name, email, password,
            }
        )
        if (response) {
            debugger
            setCaptchaVisible(true)
        }
    }
    return (
        <form className="registration-form">
            <div className="container">
                <h1>Register</h1>
                <p>Please fill in this form to create an account.</p>
                <hr/>
                <label htmlFor="name"><b>Name</b></label>
                <input 
                    type="text" 
                    placeholder="Enter Name" 
                    name="name" 
                    onChange={handleInputChange}
                />
                <label htmlFor="email"><b>Email</b></label>
                <input 
                    type="text" 
                    placeholder="Enter Email" 
                    name="email" 
                    onChange={handleInputChange}
                />
                <label htmlFor="password"><b>Password</b></label>
                <input 
                    type="password" 
                    placeholder="Enter Password" 
                    name="password" 
                    onChange={handleInputChange}
                />
                <label htmlFor="passwordrepeat"><b>Repeat Password</b></label>
                <input 
                    type="password" 
                    placeholder="Repeat Password" 
                    name="passwordrepeat"
                    onChange={handleInputChange}
                />
                <div className="clearfix">
                    <button type="submit" className="signupbtn" onClick={postRegistrationData}>
                        Register
                    </button>
                </div>
                {
                    captchaVisible && (
                        <div className="captcha-container">
                            <ReCAPTCHA
                                sitekey={RECAPTCHA_CLIENT_KEY}
                                onChange={postRegistrationData}
                            />
                        </div>
                    )
                }
            </div>
        </form>
    )
}

export default RegistrationForm
