import React, {useState} from 'react'
import ReCAPTCHA from 'react-google-recaptcha'

import '../css/RegistrationForm.css'
import { RECAPTCHA_CLIENT_KEY } from '../constants/configs'
import { registerUser } from '../utils/api'

const RegistrationForm = () => {
    const initialFormValue = { name: '', email: '', password: '', passwordrepeat: '' }
    const initialFormState = { 
        isLoading: false,
        message: '',
        isError: false
    }
    const [captchaVisible, setCaptchaVisible] = useState(false)
    const [formState, setFormState] = useState({ ...initialFormState })
    const [formValues, setFormValues] = useState({ ...initialFormValue })

    const handleInputChange = e => {
        const {name, value} = e.target
        setFormValues({...formValues, [name]: value})
    }

    const postRegistrationData = async (captchaValue) => {
        setFormState({ ...initialFormState,  isLoading: true })
        captchaVisible && setCaptchaVisible(false)
        const { name, email, password } = formValues
        const postData = {
            name, 
            email, 
            password,
            captchaValue,
        }
        // call validation
        const response = await registerUser(postData)
        if ([400, 409, 500,].includes(response.status)) {
            setFormState({ isLoading: false, isError: true, message: response })
        }
        else if(!response.success) {
            setCaptchaVisible(true)
        }
        else {
            setFormValues({ ...initialFormValue })
            setFormState({ ...initialFormState, message: response })
        }
    }

    const submitData = (e) => {
        e.preventDefault()
        postRegistrationData()
    }
    return (
        <form className="registration-form">
            <div className="container">
                <h1>Register</h1>
                <p>Please fill in this form to create an account.</p>
                <hr/>
                <label htmlFor="name"><b>Name</b></label>
                <input 
                    type="text" 
                    placeholder="Enter Name" 
                    name="name" 
                    onChange={handleInputChange}
                />
                <label htmlFor="email"><b>Email</b></label>
                <input 
                    type="text" 
                    placeholder="Enter Email" 
                    name="email" 
                    onChange={handleInputChange}
                />
                <label htmlFor="password"><b>Password</b></label>
                <input 
                    type="password" 
                    placeholder="Enter Password" 
                    name="password" 
                    onChange={handleInputChange}
                />
                <label htmlFor="passwordrepeat"><b>Repeat Password</b></label>
                <input 
                    type="password" 
                    placeholder="Repeat Password" 
                    name="passwordrepeat"
                    onChange={handleInputChange}
                />
                <div className="clearfix">
                    <button type="submit" className="signupbtn" onClick={submitData}>
                        Register
                    </button>
                </div>
                {
                    captchaVisible && (
                        <div className="captcha-container">
                            <ReCAPTCHA
                                sitekey={RECAPTCHA_CLIENT_KEY}
                                onChange={postRegistrationData}
                            />
                        </div>
                    )
                }
            </div>
        </form>
    )
}

export default RegistrationForm
